const slides = document.querySelectorAll('.slide')

for (const slide of slides) {
    slide.addEventListener('click', () => {
        clearActiveClasses()

        slide.classList.add('active')
    })
}

function clearActiveClasses() {
    slides.forEach((slide) => {
        slide.classList.remove('active')
    })
}

export class ExcelComponent extends DropComponent {
    constructor($root, options = {}) {
        super($root, options.listeners);
        this.name = options.name || ''

        this.prepare()
    }
}
export class drop extends DropComponent {
    toHTML() {
        return `
        <div class="slide  active"
     style="background-image: url(https://steamuserimages-a.akamaihd.net/ugc/573439156221441281/9DE0177BB0F3815F1C62FF9650839508B5784883/?imw=512&amp;imh=335&amp;ima=fit&amp;impolicy=Letterbox&amp;imcolor=%23000000&amp;letterbox=true);">
    <h3>Toyota Supra 1994</h3>
</div>
<div class="slide "
     style="background-image: url(https://cdn.fishki.net/upload/post/201512/01/1759298/027.jpg);">
    <h3 style="color: black;">Nissan Silvia s15</h3>
</div>
<div class=" slide "
     style="background-image: url(https://im0-tub-ru.yandex.net/i?id=dda40212a96299608d8134b52d66d402-l&ref=rim&n=13&w=640&h=640);">
    <h3>Nissan 240sx</h3>
</div>
<div class=" slide " style="background-image: url(http://img03.platesmania.com/160517/o/8450267.jpg);">
    <h3 style="color: red;">Mitsubichi GT</h3>
</div>
<div class=" slide "
     style="background-image: url(https://funart.pro/uploads/posts/2021-04/1618347467_60-funart_pro-p-yaponskie-avto-mashini-krasivo-foto-62.jpg);">
    <h3>Nissan Skyline r32</h3>
</div>
<div class=" slide "
     style="background-image: url(https://img2.goodfon.ru/wallpaper/nbig/3/b1/type-r-honda-tuning-vtec-low-mugen-civic-stance-jdm-bbs-whee.jpg)">
    <h3>Honda Civic</h3>
</div>
        `
    }
    
}
